export class NutriCalc{#foodDB;constructor(foodDB={}){this.#foodDB=foodDB} getFood(name){return this.#foodDB[name]??null} calculate(name,weight){const food=this.getFood(name);if(!food||!food.nutrients)return null;return Object.fromEntries(Object.entries(food.nutrients).map(([key,value])=>[key,+(value*weight/100).toFixed(2),]))} suggest(name){const candidates=Object.keys(this.#foodDB);return candidates.filter(item=>item.includes(name))} listFoods(){return Object.keys(this.#foodDB)} static formatResult(result,labels={}){return Object.entries(result).map(([key,val])=>`${labels[key] ?? key}: ${val}`).join("\n")}}
